var colors = require('colors/safe');


/*
activated (String): can accept:
    - "all" : print all messages;
    - "done": print only messages with type = "done";
    - "off" : no prints
 */
function Noty(activated) {
    // if active is false - noty will not write stuff in the console.
    this.active = activated;
}

/*
text (String): message to print in th user console,
type (String): color for your message like:
    - done (green color)
    - err (red color)
    - info (blue color)
    - out (standard color)
 */

Noty.prototype.log = function (text, type) {
    if (this.active == "off") return;
    switch (type){
        case "done":
            if (this.active == "all" || this.active == "done")
                console.log(colors.green(text));
            break;
        case "err":
            if (this.active == "all")
                console.log(colors.red(text));
            break;
        case "info":
            if (this.active == "all")
                console.log(colors.cyan(text));
            break;
        default:
            if (this.active == "all")
                console.log(text);
    }
}

exports.Noty = Noty;